var who;

if( typeof(chatstart) == 'undefined' ) { chatstart = true ; }
if(chatstart===true) {
    who = "Q";
} else {
    who = "A";
}

var newchatentry = {"who":"","text":""};
if( chathistory.length === 0 ) {
    // First chat, init the table
    newchatentry = {"who":"A","text":"Hello, Iâ€™m the COVID Crisis Communication Bot ready to answer your questions about COVID-19. How can I help you?"};

    // Add the question, if its not a Clear Chat button press
    if( msg.payload.length ) { 
